import React, { useState } from 'react';
import { ThemeProvider, createTheme, CssBaseline, Container, Typography, Box, Paper, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import UploadArea from './components/UploadArea';
import useUpload from './hooks/useUpload';
import useRecorder from './hooks/useRecorder';

const darkTheme = createTheme({
  palette: { mode: 'dark' },
});

function downloadText(filename, content) {
  const blob = new Blob([content], { type: 'text/plain;charset=utf-8' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = filename;
  document.body.appendChild(a);
  a.click();
  setTimeout(() => {
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }, 100);
}

function App() {
  const { uploading, progress, result, error, upload } = useUpload();
  const [file, setFile] = useState(null);
  const [recDialogOpen, setRecDialogOpen] = useState(false);
  const [recBlob, setRecBlob] = useState(null);
  const [savePath, setSavePath] = useState('');
  const { recording, startRecording, stopRecording } = useRecorder((blob) => {
    setRecBlob(blob);
    setRecDialogOpen(false);
    upload(new File([blob], 'record.wav', { type: 'audio/wav' }), savePath);
  });

  const handleFileAccepted = (file) => {
    setFile(file);
    upload(file, savePath);
  };

  const handleRecordClick = () => {
    setRecDialogOpen(true);
    startRecording();
  };

  const handleStopRecording = () => {
    stopRecording();
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Container maxWidth="sm" sx={{ py: 6 }}>
        <Typography variant="h4" align="center" gutterBottom>Whisper 語音轉文字</Typography>
        <UploadArea
          onFileAccepted={handleFileAccepted}
          uploading={uploading}
          progress={progress}
          onRecordClick={handleRecordClick}
          savePath={savePath}
          setSavePath={setSavePath}
        />
        <Dialog open={recDialogOpen} onClose={() => { setRecDialogOpen(false); stopRecording(); }}>
          <DialogTitle>即時錄音</DialogTitle>
          <DialogContent>
            <Typography>{recording ? '錄音中... 請說話' : '錄音已停止'}</Typography>
          </DialogContent>
          <DialogActions>
            {recording ? (
              <Button onClick={handleStopRecording} color="primary">停止錄音</Button>
            ) : (
              <Button onClick={() => setRecDialogOpen(false)} color="primary">關閉</Button>
            )}
          </DialogActions>
        </Dialog>
        {error && <Paper sx={{ mt: 2, p: 2, bgcolor: 'error.main', color: 'error.contrastText' }}>{error}</Paper>}
        {result && (
          <Box sx={{ mt: 4 }}>
            <Typography variant="h6">純文字結果：</Typography>
            <Paper sx={{ p: 2, mb: 2, whiteSpace: 'pre-wrap' }}>{result.text}</Paper>
            <Button
              variant="outlined"
              sx={{ mr: 2, mb: 2 }}
              onClick={() => downloadText('transcript.txt', result.text)}
              disabled={!result.text}
            >
              下載 TXT
            </Button>
            <Typography variant="h6">SRT 字幕：</Typography>
            <Paper sx={{ p: 2, whiteSpace: 'pre-wrap' }}>{result.srt}</Paper>
            <Button
              variant="outlined"
              sx={{ mr: 2, mt: 1 }}
              onClick={() => downloadText('transcript.srt', result.srt)}
              disabled={!result.srt}
            >
              下載 SRT
            </Button>
            {result.saved_files && (
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" color="success.main">已儲存：</Typography>
                {result.saved_files.text && <Typography variant="body2">TXT: {result.saved_files.text}</Typography>}
                {result.saved_files.srt && <Typography variant="body2">SRT: {result.saved_files.srt}</Typography>}
              </Box>
            )}
          </Box>
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
